//  Program make a simple calculator

//  This function adds two numbers 
var input = msg;
var eq = input.payload.eq;
var no1 = input.payload.no1;
var no2 = input.payload.no2;
var txt = ""

if (eq == 'add'){
    txt = (parseInt(no1) + parseInt(no2)).toString();
}else if (eq == 'sub'){
    txt = (parseInt(no1) - parseInt(no2)).toString();
}else if (eq == 'multi'){
    txt = (parseInt(no1) * parseInt(no2)).toString();
}else if (eq == 'div'){
    txt = (parseInt(no1) / parseInt(no2)).toString();
}

msg.payload = txt;
return msg;
